using System;

public class CPHInline
{
    public bool Execute()
    {
        string userId = args["userId"].ToString();
        string userName = args["userName"].ToString();

        string cooldownVar = $"{userId}_adventureLastPlayed";
        long lastPlayed = CPH.GetGlobalVar<long>(cooldownVar, true);
        long currentTime = DateTimeOffset.UtcNow.ToUnixTimeSeconds();
        long cooldownSeconds = 86400;

        if (lastPlayed > 0 && (currentTime - lastPlayed) < cooldownSeconds)
        {
            long remainingSeconds = cooldownSeconds - (currentTime - lastPlayed);
            long hours = remainingSeconds / 3600;
            long minutes = (remainingSeconds % 3600) / 60;
            CPH.SendMessage($"@{userName}, daily adventure ready in {hours}h {minutes}m! 🗡️");
            return false;
        }

        int currentEggs = CPH.GetPoints(userId);
        int adventureCost = 500;

        if (currentEggs < adventureCost)
        {
            CPH.SendMessage($"@{userName}, you need {adventureCost} 🥚 for an adventure! You have {currentEggs} 🥚.");
            return false;
        }

        CPH.RemovePoints(userId, adventureCost, "DnD Adventure Entry");
        CPH.SetGlobalVar(cooldownVar, currentTime, true);

        Random rnd = new Random();
        string[] savingThrows = { "STR", "DEX", "CON", "INT", "WIS", "CHA", "DEATH" };
        string saveType = savingThrows[rnd.Next(savingThrows.Length)];
        int roll = rnd.Next(1, 21);

        string scenario = GetScenario(saveType, roll, rnd);
        int eggReward = 0;
        int tokenReward = 0;
        string tokenType = "";

        if (roll == 1)
        {
            eggReward = -rnd.Next(200, 301);
            scenario += $" Lose {Math.Abs(eggReward)} 🥚!";
        }
        else if (roll <= 5)
        {
            eggReward = -rnd.Next(50, 101);
            scenario += $" Lose {Math.Abs(eggReward)} 🥚.";
        }
        else if (roll <= 10)
        {
            eggReward = rnd.Next(100, 301);
            scenario += $" Gain {eggReward} 🥚!";
        }
        else if (roll <= 15)
        {
            eggReward = rnd.Next(400, 601);
            if (rnd.Next(1, 3) == 1)
            {
                tokenReward = 1;
                tokenType = GetRandomToken(rnd);
            }
            scenario += tokenReward > 0 ? $" {eggReward} 🥚 + 1 {tokenType}!" : $" {eggReward} 🥚!";
        }
        else if (roll <= 19)
        {
            eggReward = rnd.Next(700, 901);
            tokenReward = 1;
            tokenType = GetRandomToken(rnd);
            scenario += $" {eggReward} 🥚 + 1 {tokenType}!";
        }
        else
        {
            eggReward = rnd.Next(1000, 1501);
            tokenReward = 2;
            tokenType = GetRandomToken(rnd);
            scenario += $" CRIT! {eggReward} 🥚 + 2 {tokenType}!";
        }

        if (eggReward > 0) CPH.AddPoints(userId, eggReward, "DnD Adventure Win");
        else if (eggReward < 0) CPH.RemovePoints(userId, Math.Abs(eggReward), "DnD Adventure Loss");

        if (tokenReward > 0)
        {
            string tokenVar = $"{userId}_{tokenType}";
            int currentTokens = CPH.GetGlobalVar<int>(tokenVar, true);
            CPH.SetGlobalVar(tokenVar, currentTokens + tokenReward, true);
        }

        int totalAdventures = CPH.GetGlobalVar<int>($"{userId}_totalAdventures", true);
        CPH.SetGlobalVar($"{userId}_totalAdventures", totalAdventures + 1, true);

        CPH.SendMessage($"🎲 @{userName} | {saveType} (Roll: {roll}) | {scenario}");
        return true;
    }

    private string GetScenario(string saveType, int roll, Random rnd)
    {
        string[] scenarios;
        string base_scenario;

        switch (saveType)
        {
            case "STR":
                scenarios = new string[] { "A boulder blocks your path!", "You must lift a portcullis!", "An iron door stands before you!", "You wrestle a troll!", "A bridge collapses!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Catastrophic fail.";
                if (roll <= 5) return base_scenario + " Can't overcome.";
                if (roll <= 10) return base_scenario + " Barely manage.";
                if (roll <= 15) return base_scenario + " Success!";
                if (roll <= 19) return base_scenario + " Dominate!";
                return base_scenario + " Godly might!";
            case "DEX":
                scenarios = new string[] { "Poison darts!", "Floor collapses!", "Swinging axes!", "Must leap across!", "Arrows fly!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Full hit.";
                if (roll <= 5) return base_scenario + " Too slow.";
                if (roll <= 10) return base_scenario + " Narrowly avoid.";
                if (roll <= 15) return base_scenario + " Graceful dodge!";
                if (roll <= 19) return base_scenario + " Acrobatic!";
                return base_scenario + " Impossible backflip!";
            case "CON":
                scenarios = new string[] { "Toxic gas!", "Venomous bite!", "Cursed water!", "Freezing cold!", "Disease!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Body fails.";
                if (roll <= 5) return base_scenario + " Suffer badly.";
                if (roll <= 10) return base_scenario + " Weakened.";
                if (roll <= 15) return base_scenario + " Resist!";
                if (roll <= 19) return base_scenario + " Strong!";
                return base_scenario + " Immune!";
            case "INT":
                scenarios = new string[] { "Ancient puzzle!", "Cryptic riddle!", "Magical trap!", "Mysterious tome!", "Illusion!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Disaster.";
                if (roll <= 5) return base_scenario + " Can't solve.";
                if (roll <= 10) return base_scenario + " Struggle through.";
                if (roll <= 15) return base_scenario + " Clever!";
                if (roll <= 19) return base_scenario + " Genius!";
                return base_scenario + " Brilliant!";
            case "WIS":
                scenarios = new string[] { "Siren's song!", "Mind spores!", "Madness whispers!", "Hidden danger!", "Evil presence!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Succumb.";
                if (roll <= 5) return base_scenario + " Influenced.";
                if (roll <= 10) return base_scenario + " Resist strain.";
                if (roll <= 15) return base_scenario + " See through!";
                if (roll <= 19) return base_scenario + " Fortitude!";
                return base_scenario + " Turn it back!";
            case "CHA":
                scenarios = new string[] { "Hostile bandits!", "Dragon demands!", "Guards question!", "Merchant haggles!", "Rival challenges!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Gravely offend.";
                if (roll <= 5) return base_scenario + " Make it worse.";
                if (roll <= 10) return base_scenario + " Barely convince.";
                if (roll <= 15) return base_scenario + " Charm them!";
                if (roll <= 19) return base_scenario + " New allies!";
                return base_scenario + " Legendary!";
            case "DEATH":
                scenarios = new string[] { "On the brink!", "Life fades!", "Slipping away!", "Darkness closes!", "Heartbeat slows!" };
                base_scenario = scenarios[rnd.Next(scenarios.Length)];
                if (roll == 1) return base_scenario + " Fail to stabilize.";
                if (roll <= 5) return base_scenario + " Barely conscious.";
                if (roll <= 10) return base_scenario + " Stabilize weakly.";
                if (roll <= 15) return base_scenario + " Recover!";
                if (roll <= 19) return base_scenario + " Spring back!";
                return base_scenario + " Defy death!";
            default:
                return "Mysterious event.";
        }
    }

    private string GetRandomToken(Random rnd)
    {
        string[] tokens = { "MysteryEgg", "DiceEgg", "DuelEgg" };
        return tokens[rnd.Next(tokens.Length)];
    }
